import React from "react";
import { Container, ListGroup, Alert, Badge } from "react-bootstrap";

class DishComments extends React.Component {
  state = {
    comments: [],
    selectedDish: [],
  };

  componentDidMount = () => {
    this.setState({
      selectedDish: this.props.selectedDish,
      comments: this.props.comments,
    });
  };

  render() {
    console.log(this.state);
    return (
      <Container>
        {this.selectedDish ? (
          <ListGroup className="mt-5 mb-5">
            <h2 className="text-center mb-3">{this.selectedDish.name}</h2>
            {this.selectedDish.comments.map((comment, index) => {
              let variant = "";

              switch (this.comment.rating) {
                case 1:
                  variant = "danger";
                  break;
                case 2:
                  variant = "warning";
                  break;
                case 3:
                  variant = "secondary";
                  break;
                default:
                  variant = "success";
                  break;
              }

              return (
                <ListGroup.Item key={`list-item-${index}`}>
                  {comment.author}: {comment.comment} |{" "}
                  <Badge pill variant={variant}>
                    {comment.rating}
                  </Badge>
                </ListGroup.Item>
              );
            })}
          </ListGroup>
        ) : (
          <Alert variant="danger" className="mt-5 ">
            No dish selected, please click on a Dish to show the comments
          </Alert>
        )}
      </Container>
    );
  }
}

export default DishComments;
